--- CREATE A DATABASE NAMED [destination] (For dbt project)
-- CREATE SCHEMA named origin
USE [destination]

CREATE TABLE origin.sales (
sales_id int not null,
seller_id int not null,
item_description varchar(max) not null,
value decimal(28,14) not null,
date date not null,
supervisor_name varchar(max) not null
);




CREATE TABLE origin.sellers (
sellers_id int not null,
name varchar(max) not null,
adress_id int not null,
phone_id int not null
)


CREATE TABLE origin.address (
sellers_adress_id int not null,
street varchar(max) not null,
city varchar(max) not null,
state varchar(max) not null,
country varchar(max) not null,
number varchar(max) not null
)


CREATE TABLE origin.phone (
sellers_phone_id int not null,
phone_number varchar(max) not null,
phone_type int not null
)


